const API_URL = 'http://localhost:8080';

export const authService = {
    async login(email, password) {
        try {
            console.log('Intentando login con:', { email });
            const response = await fetch(`${API_URL}/api/login.php`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });
            
            const data = await response.json();
            console.log('Respuesta del servidor:', data);
            
            if (!response.ok) {
                throw new Error(data.message || 'Error en la respuesta del servidor');
            }
            return data;
        } catch (error) {
            console.error('Error en login:', error);
            throw error;
        }
    },

    async register(username, email, password) {
        try {
            console.log('Intentando registro con:', { username, email });
            const response = await fetch(`${API_URL}/api/register.php`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, email, password }),
            });
            
            const data = await response.json();
            console.log('Respuesta del servidor:', data);
            
            if (!response.ok) {
                throw new Error(data.message || 'Error en el registro');
            }
            return data;
        } catch (error) {
            console.error('Error en registro:', error);
            throw error;
        }
    }
};

export const productService = {
    async getFilteredProducts(filters = {}) {
        try {
            const queryParams = new URLSearchParams();
            
            if (filters.categories?.length) {
                queryParams.append('category', filters.categories.join(','));
            }
            if (filters.conditions?.length) {
                queryParams.append('condition', filters.conditions.join(','));
            }
            if (filters.minPrice) {
                queryParams.append('minPrice', filters.minPrice);
            }
            if (filters.maxPrice) {
                queryParams.append('maxPrice', filters.maxPrice);
            }

            const response = await fetch(`${API_URL}/api/products.php?${queryParams}`);
            if (!response.ok) {
                throw new Error('Error al obtener productos');
            }
            return await response.json();
        } catch (error) {
            console.error('Error al obtener productos:', error);
            throw error;
        }
    },

    async createProduct(productData) {
        try {
            const response = await fetch(`${API_URL}/api/products.php`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(productData),
            });
            if (!response.ok) {
                throw new Error('Error al crear producto');
            }
            return await response.json();
        } catch (error) {
            console.error('Error al crear producto:', error);
            throw error;
        }
    }
};

export const profileService = {
    async getProfile(userId) {
        try {
            const response = await fetch(`${API_URL}/api/profile.php?user_id=${userId}`);
            if (!response.ok) {
                throw new Error('Error al obtener perfil');
            }
            return await response.json();
        } catch (error) {
            console.error('Error al obtener perfil:', error);
            throw error;
        }
    },

    async updateProfile(profileData) {
        try {
            const response = await fetch(`${API_URL}/api/profile.php`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(profileData),
            });
            if (!response.ok) {
                throw new Error('Error al actualizar perfil');
            }
            return await response.json();
        } catch (error) {
            console.error('Error al actualizar perfil:', error);
            throw error;
        }
    }
};

export const uploadService = {
    async uploadImage(file) {
        const formData = new FormData();
        formData.append('image', file);

        const response = await fetch(`${API_URL}/upload.php`, {
            method: 'POST',
            body: formData,
        });
        return response.json();
    }
};
