import { ref } from 'vue';
import { collection, addDoc } from "firebase/firestore";
import { db } from '../main.js'; // Asegúrate de importar db desde tu configuración de Firebase

export default {
  __name: 'FeedPage',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const title = ref("");
    const description = ref("");
    const submitData = async () => {
      try {
        // Añade un nuevo documento a la colección "posts"
        await addDoc(collection(db, "posts"), {
          title: title.value,
          description: description.value,
          createdAt: new Date()
        });
        alert("Datos enviados correctamente");
        title.value = ""; // Limpiar campos después de enviar
        description.value = "";
      } catch (e) {
        console.error("Error al añadir el documento: ", e);
        alert("Hubo un error al enviar los datos");
      }
    };
    const __returned__ = {
      title,
      description,
      submitData,
      ref,
      get collection() {
        return collection;
      },
      get addDoc() {
        return addDoc;
      },
      get db() {
        return db;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
