import { createRouter, createWebHistory } from 'vue-router'
import PrincipalPage from '../views/PrincipalPage.vue'
import LoginPage from '../views/LoginPage.vue'
import RegistroPage from '../views/RegistroPage.vue'
import CrearProducto from '../views/CrearProducto.vue'
import ProductosPage from '../views/ProductosPage.vue'
import DetallesPage from '../views/DetallesPage.vue'
import PerfilUser from '../views/PerfilUser.vue'

const routes = [
  {
    path: '/',
    name: 'Principal',
    component: PrincipalPage
  },
  {
    path: '/login',
    name: 'Login',
    component: LoginPage
  },
  {
    path: '/registro',
    name: 'Registro',
    component: RegistroPage
  },
  {
    path: '/crear-producto',
    name: 'CrearProducto',
    component: CrearProducto,
    meta: { requiresAuth: true }
  },
  {
    path: '/productos',
    name: 'Productos',
    component: ProductosPage
  },
  {
    path: '/producto/:id',
    name: 'DetallesProducto',
    component: DetallesPage
  },
  {
    path: '/perfil',
    name: 'Perfil',
    component: PerfilUser,
    meta: { requiresAuth: true }
  }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

// Navegación protegida
router.beforeEach((to, from, next) => {
  const isAuthenticated = localStorage.getItem('token')
  
  if (to.meta.requiresAuth && !isAuthenticated) {
    next('/login')
  } else {
    next()
  }
})

export default router
