import { createApp } from 'vue'
import App from './App.vue'
import router from "./router"
// Import the functions you need from the SDKs you need
import { initializeApp } from "firebase/app";
import { getFirestore } from "firebase/firestore";
// TODO: Add SDKs for Firebase products that you want to use
// https://firebase.google.com/docs/web/setup#available-libraries

// Your web app's Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyCGiv-WriAFpjLKsW7TfScKAhUjrDrtcC0",
  authDomain: "auth-convue.firebaseapp.com",
  projectId: "auth-convue",
  storageBucket: "auth-convue.appspot.com",
  messagingSenderId: "829820239213",
  appId: "1:829820239213:web:a8c21de854835365ed2e64"
};

// Initialize Firebase

const app = initializeApp(firebaseConfig);
const db = getFirestore(app);  // Asegúrate de que Firebase esté inicializado antes de obtener Firestore

createApp(App).mount('#app');

export {db};

app.use(router)
