import { createStore } from 'vuex'
import axios from 'axios'

const API_URL = 'http://localhost:8090/api'

export default createStore({
  state: {
    user: null,
    isAuthenticated: false,
    products: [],
    loading: false,
    error: null
  },
  mutations: {
    setUser(state, user) {
      state.user = user
      state.isAuthenticated = !!user
    },
    setProducts(state, products) {
      state.products = products
    },
    setLoading(state, status) {
      state.loading = status
    },
    setError(state, error) {
      state.error = error
    },
    addProduct(state, product) {
      state.products.push(product)
    },
    updateProduct(state, updatedProduct) {
      const index = state.products.findIndex(p => p.id === updatedProduct.id)
      if (index !== -1) {
        state.products.splice(index, 1, updatedProduct)
      }
    },
    deleteProduct(state, productId) {
      state.products = state.products.filter(p => p.id !== productId)
    }
  },
  actions: {
    async login({ commit }, credentials) {
      try {
        const formData = new FormData()
        formData.append('username', credentials.email)
        formData.append('password', credentials.password)
        const response = await axios.post(`${API_URL}/token`, formData)
        
        if (response.data.access_token) {
          localStorage.setItem('token', response.data.access_token)
          commit('setUser', { email: credentials.email })
          return true
        }
      } catch (error) {
        commit('setError', error.response?.data?.detail || 'Error en el inicio de sesión')
        throw error
      }
    },

    async register({ commit }, userData) {
      try {
        await axios.post(`${API_URL}/users/`, userData)
        return true
      } catch (error) {
        commit('setError', error.response?.data?.detail || 'Error en el registro')
        throw error
      }
    },

    logout({ commit }) {
      localStorage.removeItem('token')
      commit('setUser', null)
    },

    async fetchProducts({ commit }) {
      try {
        commit('setLoading', true)
        const response = await axios.get(`${API_URL}/products`)
        commit('setProducts', response.data)
      } catch (error) {
        commit('setError', 'Error al cargar los productos')
      } finally {
        commit('setLoading', false)
      }
    },

    async createProduct({ commit }, productData) {
      try {
        const token = localStorage.getItem('token')
        const response = await axios.post(`${API_URL}/products`, productData, {
          headers: { Authorization: `Bearer ${token}` }
        })
        commit('addProduct', response.data)
        return response.data
      } catch (error) {
        commit('setError', 'Error al crear el producto')
        throw error
      }
    },

    async updateProduct({ commit }, { id, data }) {
      try {
        const token = localStorage.getItem('token')
        const response = await axios.put(`${API_URL}/products/${id}`, data, {
          headers: { Authorization: `Bearer ${token}` }
        })
        commit('updateProduct', response.data)
        return response.data
      } catch (error) {
        commit('setError', 'Error al actualizar el producto')
        throw error
      }
    },

    async deleteProduct({ commit }, productId) {
      try {
        const token = localStorage.getItem('token')
        await axios.delete(`${API_URL}/products/${productId}`, {
          headers: { Authorization: `Bearer ${token}` }
        })
        commit('deleteProduct', productId)
      } catch (error) {
        commit('setError', 'Error al eliminar el producto')
        throw error
      }
    }
  },
  getters: {
    isAuthenticated: state => state.isAuthenticated,
    currentUser: state => state.user,
    allProducts: state => state.products,
    isLoading: state => state.loading,
    error: state => state.error
  }
})
